//		long tic = System.currentTimeMillis();
		int n = sc.nextInt();
		int k = sc.nextInt();
		String s = sc.next();
		String s2 = s.substring(0,k);
		boolean flag = true;
		for(int i = k;i<n;i++) {
			if(s.charAt(i)<s2.charAt((i)%k)) {
				break;
			}else if(s.charAt(i)>s2.charAt((i)%k)) {
