import java.util.HashSet;
import java.util.Scanner;
/**
 * 第四题
 * 给定一个数n(n<1000000000) 和 一个数组a[m]  m (1<=m<=10,1<=a[i]<=20) 
 * 如果n对于一个数 ai有n%ai==0,则认为两个数相关。
 * 问在 1 - n中有多少个相关
 * 样例

10 2
2
3

5

 * 这题是考容斥原理的题。当然要提前处理一下数值，不能有倍数关系。
 * 去除倍数关系之后进行计算值就可
 */
public class T4 {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		long n = sc.nextLong();
		int m = sc.nextInt();
		HashSet<Integer> hs = new HashSet<Integer>();
		long ans = 0;
		for(int i = 0;i < m; i++) {
			hs.add(sc.nextInt());
		}
		int arr[] = new int[hs.size()];
		int temp = 0;
		for (Integer integer : hs) {
			arr[temp++] = integer;
		}
		for(int i = 0;i<arr.length;i++) {
			for(int j = i+1;j<arr.length;j++) {
				if(Math.max(arr[i], arr[j])%Math.min(arr[i], arr[j])==0) {
					hs.remove(Math.max(arr[i], arr[j]));
				}
			}
		}
		arr = new int[hs.size()];
		temp = 0;
		for (Integer integer : hs) {
			arr[temp++] = integer;
		}
		for(int i = 1;i<1<<arr.length;i++) {
			int num = 0;
			long test = 1;
			for(int t = i,j=0;t!=0;t>>=1,j++) {
				if((t&1)!=0) {
					num++;
					test *= arr[j];
				}
			}
			if((num&1)==0) {
				ans-=n/test;
			}else {
				ans+=n/test;
			}
		}
		System.out.println(ans);
	}
	static int gcd(int a, int b) {
		if(b==0) {
			return a;
		}else {
			return gcd(b,a%b);
		}
	}
}
