		for (Integer integer : hs) {
			arr[temp++] = integer;
		}
		for(int i = 1;i<1<<arr.length;i++) {
			int num = 0;
			long test = 1;
			for(int t = i,j=0;t!=0;t>>=1,j++) {
				if((t&1)!=0) {
					num++;
					test *= arr[j];
				}
			}
			if((num&1)==0) {
				ans-=n/test;
			}else {
				ans+=n/test;
			}
		}
		System.out.println(ans);
	}
	static int gcd(int a, int b) {
		if(b==0) {
			return a;
		}else {
			return gcd(b,a%b);
		}
	}
}
