    static StreamTokenizer st = new StreamTokenizer(new BufferedInputStream(System.in));

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long l = sc.nextLong();
        long r = sc.nextLong();
        int t = sc.nextInt();
        int rc[] = new int[t];
        for (int i = 0; i < t; i++) {
            rc[i] = sc.nextInt();
        }
        l--;
        long cont1 = 0;
        for (int i = 1; i < (1 << t); i++) {
            int cont = 0;
            long te = 1;
            for (int j = 0; j < t; j++) {
                if (((i) & (1 << j)) != 0) {
                    cont++;
                    te *= rc[j];
                    if(te>r) {
                        break;
                    }
