import java.io.BufferedInputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.StreamTokenizer;
import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.PriorityQueue;
import java.util.Scanner;

public class Main {

	static StreamTokenizer st = new StreamTokenizer(new BufferedInputStream(System.in));

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = nextNum();
		int m = nextNum();
		int k = nextNum();
		long t = nextNum();
		int T[] = new int[n + 1];
		PriorityQueue<Node> pq = new PriorityQueue<>();
		for (int i = 1; i <= n; i++) {
			T[i] = nextNum();
			pq.add(new Node(0, i, T[i], 0));
		}
		boolean has[] = new boolean[n + 1];
		boolean use[] = new boolean[m + 1];
		has[0] = true;
		int K[] = new int[k];
		for (int i = 0; i < k; i++) {
			K[i] = nextNum();
			has[K[i]] = true;
		}

		ArrayList<ArrayList<Node>> al = new ArrayList<>();
		for (int i = 0; i <= n; i++) {
			al.add(new ArrayList<>());
		}
		int flag = k;
		for (int i = 1; i <= m; i++) {
			Node node = new Node(nextNum(), nextNum(), nextNum(), i);
			al.get(node.x).add(node);
			al.get(node.y).add(node);
		}

		for (int i = 0; i < k; i++) {
			for (Node node : al.get(K[i])) {
				if (!use[node.id]) {
					pq.add(node);
					use[node.id] = true;
				}
			}
		}

		long ans = 0;
		while (!pq.isEmpty()) {
			Node te = pq.poll();
			if (has[te.x] && has[te.y]) {
				continue;
			} else if (has[te.x]) {
				flag++;
				ans += te.h;
				has[te.y] = true;
				for (Node node : al.get(te.y)) {
					if ((has[node.x] && has[node.y]) || use[node.id]) {
						continue;
					} else {
						pq.add(node);
						if(node.id!=0) {
							use[node.id] = true;
						}
					}
				}
			} else {
				flag++;
				ans += te.h;
				has[te.x] = true;
				for (Node node : al.get(te.x)) {
					if ((has[node.x] && has[node.y]) || use[node.id]) {
						continue;
					} else {
						pq.add(node);
						if(node.id!=0) {
							use[node.id] = true;
						}
					}
				}
			}
			if (flag >= n) {
				break;
			}
			if (ans > t) {
				System.out.println("No");
				return;
			}
		}
		System.out.println("Yes");
		return;
	}
	private static int nextNum() {
		try {
			st.nextToken();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return (int) st.nval;

	}
}

class Node implements Comparable<Node> {
	int x;
	int y;
	int h;
	int id;

	public Node(int x, int y, int h, int id) {
		super();
		this.x = x;
		this.y = y;
		this.h = h;
		this.id = id;
	}

	@Override
	public String toString() {
		return "Node [x=" + x + ", y=" + y + ", h=" + h + "]";
	}

	@Override
	public int compareTo(Node o) {

		return h - o.h;
	}

}
