import java.util.Scanner;
public class T3 {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int a[] = new int[n+1];
		int ans = 0;
		for(int i = 1;i<=n;i++) {
			a[i] = sc.nextInt();
			ans^=a[i];
