import java.util.Scanner;
/**
 * 第四题
 * 公司有n（n<24）个人，对于每个人只能有一个直系上司
 * 对于每个人他的下属和他自己的总数是ai
 * 问能否构成一个关系满足，
 * 每个人最少有两个下属，或者没有下属。
 * 对于除最高职位的人，都有且只有一个直系上司。
 * 问能否根据ai建立关系树。
 * 
 * 就是一个搜索加上回溯问题，把最高的当做根节点，然后找到和为父节点的值，将这几个当为父节点，进行递归处理。
 * 如果对于排序好的，从高到底的安排节点，不进行回溯，能过45%。会丧失对于较大的节点是另外更大节点的儿子节点，能够组成数的情况。
 * 
 */
public class T4 {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		while (sc.hasNext()) {
			int n = sc.nextInt();
			int arr[] = new int[n];
			for (int i = 0; i < n; i++) {
				arr[i] = sc.nextInt();
			}
			Arrays.sort(arr);
			boolean flag = sl(arr[arr.length - 1], 0, 0, arr);
			if (flag) {
				System.out.println("YES");
			} else {
				System.out.println("NO");
			}
		}
	}

	static boolean sl(int f, int z, int used, int arr[]) {
		int sum = 0;
		int cont = 0;
		if(f==1) {
			return true;
		}
		int use = used;
		for (int j = arr.length - 2; j >= 0; j--) {
			for (int i = j; i >= 0; i--) {
				if ((use & (1 << i)) == 0 && sum + arr[i]+1 <= f) {
					sum += arr[i];
					use |= (1 << i);
					z |= (1 << i);
					cont++;
					if (sum + 1 == f) {
						break;
					}
				}
			}
			if (sum + 1 != f) {
				return false;
			}
			boolean flag = false;
			if (cont > 1) {
				for (int i = 1; i < arr.length; i++) {
					if ((z & 1 << i) != 0) {
						if (arr[i] != 1) {
							flag = sl(arr[i], 0, use, arr);
							if (!flag) {
								break;
							}
						} else {
							use |= 1 << i;
						}

					}
				}
			}else {
				return false;
			}
//			for(int i = 1;i<arr.length;i++) {
//				if((use&(1<<i))==0) {
//					return false;
//				}
//			}
		}
		return true;
	}
}
/*
 * 3 1 1 3 2 1 2
 * 
 * YES NO
 */
